interface CandidateContextType {
    candidates: Candidate[];
    getCandidateById: (id: number) => Candidate | undefined;
    addCandidate: (candidate: Omit<Candidate, "id">) => void;
    updateCandidate: (id: number, candidate: Omit<Candidate, "id">) => void;
    deleteCandidate: (id: number) => void;
}

const CandidateContext = createContext<CandidateContextType | undefined>(undefined);

export function CandidateProvider({ children }: { children: ReactNode }) {
    const [candidates, setCandidates] = useState<Candidate[]>(initialCandidates);

    const getCandidateById = (id: number) => {
        return candidates.find(candidate => candidate.id === id);
    };

    const addCandidate = (candidate: Omit<Candidate, "id">) => {
        const newId = Math.max(...candidates.map(c => c.id)) + 1;
        setCandidates([...candidates, { ...candidate, id: newId }]);
    };

    const updateCandidate = (id: number, candidateData: Omit<Candidate, "id">) => {
        setCandidates(candidates.map(candidate =>
            candidate.id === id ? { ...candidateData, id } : candidate
        ));
    };

    const deleteCandidate = (id: number) => {
        setCandidates(candidates.filter(candidate => candidate.id !== id));
    };

    return (
        <CandidateContext.Provider value={{
            candidates,
            getCandidateById,
            addCandidate,
            updateCandidate,
            deleteCandidate
        }}>
            {children}
        </CandidateContext.Provider>
    );
}

export function useCandidates() {
    const context = useContext(CandidateContext);
    if (context === undefined) {
        throw new Error('useCandidates must be used within a CandidateProvider');
    }
    return context;
}
